// 2. Use the nuget package manager to install the package Google.Apis.bigquery.v2
// 3. This sample uses Application Default Credentials for Auth. If not already done, install gcloud CLI from 
//    https://cloud.google.com/sdk/ and run 'gcloud auth login'

using Google.Apis.Bigquery.v2;
using Google.Apis.Bigquery.v2.Data;
using Google.Apis.Auth.OAuth2;
using Google.Apis.Services;
using System;

class BigquerySample
{
    static void Main()
    {
        // Authentication is provided by gcloud tool when running locally
        // and by built-in service accounts when running on GAE, GCE or GKE.
        GoogleCredential credential = GoogleCredential.GetApplicationDefaultAsync().Result;

        // The createScopedRequired method returns true when running on GAE or a local developer
        // machine. In that case, the desired scopes must be passed in manually. When the code is
        // running in GCE, GKE or a Managed VM, the scopes are pulled from the GCE metadata server.
        // See https://developers.google.com/identity/protocols/application-default-credentials for more information.
        if (credential.IsCreateScopedRequired)
        {
            credential = credential.CreateScoped(new[] { "https://www.googleapis.com/auth/cloud-platform" });
        }

        var bigqueryService = new BigqueryService(new BaseClientService.Initializer() { HttpClientInitializer = credential });

        var request = bigqueryService.Tables.Get("example-project", "example-datasetId", "example-tableId");
        var response = request.Execute();
    }
}
